    private List<DetailAchievData> detailAchievDatas { get; set; }
    HttpClient client = new HttpClient();

    public bool titleMode { get; set; } = false;
    public bool comCountMode { get; set; } = false;
    public bool corCountMode { get; set; } = false;
    public bool curMarkMode { get; set; } = false;
    public bool tryNumMode { get; set; } = false;
    public bool saveDateMode { get; set; } = false;
    public List<bool> descmodearr { get; set; } = new List<bool>();

    protected async override Task OnInitializedAsync()
    {
        string curuser = await localStore.GetItemAsync<string>("currentuser");
        HttpResponseMessage response = await client.GetAsync("https://localhost:5001/api/achievdatas/detail/" + curuser);
        if (response.IsSuccessStatusCode)
        {
            detailAchievDatas = await response.Content.ReadAsAsync<List<DetailAchievData>>();
            for(int i = 0; i < detailAchievDatas.Count; i++)
            {
                descmodearr.Add(false);
            }
        }
    }

    public void byTitleSort()
    {
        if (titleMode)
        {
            detailAchievDatas.Sort((a, b) => a.TrainingTestTitle.CompareTo(b.TrainingTestTitle));
        }
        else
        {
            detailAchievDatas.Sort((a, b) => b.TrainingTestTitle.CompareTo(a.TrainingTestTitle));
        }
        titleMode = !titleMode;
    }

    public void byComCountSort()
    {
        if (comCountMode)
        {
            detailAchievDatas.Sort((a, b) => a.CompletedCount.CompareTo(b.CompletedCount));
        }
        else
        {
            detailAchievDatas.Sort((a, b) => b.CompletedCount.CompareTo(a.CompletedCount));
        }
        comCountMode = !comCountMode;
    }

    public void byCorCountSort()
    {
        if (corCountMode)
        {
            detailAchievDatas.Sort((a, b) => a.CorrectCount.CompareTo(b.CorrectCount));
        }
        else
        {
            detailAchievDatas.Sort((a, b) => b.CorrectCount.CompareTo(a.CorrectCount));
        }
        corCountMode = !corCountMode;
    }

    public void byCurMarkSort()
    {
        if (curMarkMode)
        {
            detailAchievDatas.Sort((a, b) => (a.CurrentMark > b.CurrentMark) ? 1 : (b.CurrentMark > a.CurrentMark) ? -1 : 0);
        }
        else
        {
            detailAchievDatas.Sort((a, b) => (a.CurrentMark < b.CurrentMark) ? 1 : (b.CurrentMark < a.CurrentMark) ? -1 : 0);
        }
        curMarkMode = !curMarkMode;
    }

    public void byTryNumSort()
    {
        if (tryNumMode)
        {
            detailAchievDatas.Sort((a, b) => (a.TryCount > b.TryCount) ? 1 : (b.TryCount > a.TryCount) ? -1 : 0);
        }
        else
        {
            detailAchievDatas.Sort((a, b) => (a.TryCount < b.TryCount) ? 1 : (b.TryCount < a.TryCount) ? -1 : 0);
        }
        tryNumMode = !tryNumMode;
    }

    public void bySaveDateSort()
    {
        if (saveDateMode)
        {
            detailAchievDatas.Sort((a, b) => (a.SaveDate > b.SaveDate) ? 1 : (b.SaveDate > a.SaveDate) ? -1 : 0);
        }
        else
        {
            detailAchievDatas.Sort((a, b) => (a.SaveDate < b.SaveDate) ? 1 : (b.SaveDate < a.SaveDate) ? -1 : 0);
        }
        saveDateMode = !saveDateMode;
    }

    public void changemodearr(DetailAchievData d)
    {
        descmodearr[detailAchievDatas.IndexOf(d)] = !descmodearr[detailAchievDatas.IndexOf(d)];
    }

