    {
        var cssClass = new List<string>();
        cssClass.Add("fas");
        cssClass.Add("fa-2x");
        if (titleMode)
        {
            cssClass.Add("fa-caret-up");
        }
        else
        {
            cssClass.Add("fa-caret-down");
        }
        return string.Join(' ', cssClass);
